// /frontend/src/index.js
import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

function App() {
  const [text, setText] = useState('');
  const [improvedText, setImprovedText] = useState('Wait a minute, please...'); // 初期値を設定
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (!isLoading && !improvedText) {
      setImprovedText('Wait a minute, please...'); // isLoadingがfalseでimprovedTextが空のときにメッセージを表示
    }
  }, [isLoading, improvedText]);

  const improveText = async () => {
    setIsLoading(true);
    setImprovedText(''); // ボタンがクリックされたときにimprovedTextを空にする
    try {
      const response = await axios.post('http://localhost:5000/improve_text', { text });
      setImprovedText(response.data.improved_text);
    } catch (error) {
      console.error('Error improving text:', error);
    }
    setIsLoading(false);
  };

  return (
    <div>
      <textarea style={{ marginBottom: '1em' }} value={text} onChange={e => setText(e.target.value)} />
      <button style={{ marginBottom: '1em' }} onClick={improveText}>Improve Text</button>
      <div style={{ width: '100%', wordWrap: 'break-word' }}>{isLoading ? 'Loading...' : improvedText}</div>
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById('root'));
