import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

function App() {
  const [text, setText] = useState('');
  const [improvedText, setImprovedText] = useState('');

  const improveText = async () => {
    try {
      const response = await axios.post('/improve_text', { text });
      setImprovedText(response.data.improved_text);
    } catch (error) {
      console.error('Error improving text:', error);
    }
  };

  return (
    <div>
      <textarea style={{ width: '100%', marginBottom: '1em' }} value={text} onChange={e => setText(e.target.value)} />
      <button style={{ width: '100%', marginBottom: '1em' }} onClick={improveText}>Improve Text</button>
      <div style={{ width: '100%', wordWrap: 'break-word' }}>{improvedText}</div>
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById('root'));
